	     $uc =~ tr,\0,,d,
	     $uc
	    ) :
	    ($l >= 0xeb00 and $l <= 0xeeff) ?
	    (
	     $uc = substr($ea2u2s, ($l - 0xeb00) * 4, 4),
	     $uc =~ tr,\0,,d,
	     $uc
	    ) :
	    ($l >= 0xe000 and $l <= 0xfcff) ?
	    (
	     $uc = substr($s2u_table, ($l - 0xe000 + 0x1f3f) * 3, 3),
	     $uc =~ tr,\0,,d,
	     $uc
	    ) :
	    ($l < 0x80) ?
	    chr($l) :
